mapper.select:
SELECT ROWNUM, A.* FROM ( SELECT a.SEQ, a.RGST_DATE as reg_dt, a.MDCD as mildsc, DECODE(A.MDCD , 'A', '국방부' , 'B', '육군' , 'C', '해군' , 'D', '공군') AS MILDSC_NM, a.ID, a.FULNM AS nm, a.dept_cd, b.dept_nm AS DEPT_NM, b.WHL_DEPT_NM as full_dept_nm, a.SRVNO AS mil_no, a.RSPSBLT_BIZNES_NM AS rspsblt_biznes, a.RANK_NM AS RANK, a.RSPONM AS rspofc_nm, a.telno, a.mpno, a.EMAIL, b.telno AS dept_telno FROM tbl_user_n a LEFT JOIN tbl_dept_n b ON a.dept_cd=b.dept_cd AND a.MDCD=b.MDCD WHERE 1=1
AND a.MDCD = #{mildsc}
AND a.MDCD = 'A'
AND ( a.id like '%' || #{searchTxt} ||'%' OR a.FULNM like '%' || #{searchTxt} || '%' )
ORDER BY a.seq ) A WHERE 1=1 AND ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

SELECT ROWNUM, A.* FROM ( SELECT SEQ , BLTNBD_CD as BOARD_CD , TITLE , CTNT as CONTENT , OPNPBL_YN as STATE , INQRY_NMBR as CNT , RGST_DTTM as REG_DT , RGST_ID as REG_ID , RGST_MDCD as REG_MILDSC , MDFC_DTTM as UPT_DT , MDFC_ID as UPT_ID , MDFC_MDCD as UPT_MILDSC , FILEATCH_NUM , (SELECT COUNT(*) FROM tbl_board_atch A WHERE A.tbl_pk = FILEATCH_NUM) AS ATCH_CNT FROM TBL_BOARD_n WHERE 1=1 AND BLTNBD_CD = #{boardCd}
AND ( TITLE like '%' || #{searchTxt} ||'%' OR CTNT like '%' || #{searchTxt} || '%' )
ORDER BY seq desc ) A WHERE 1=1 AND ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

mapper.insert:
	
	INSERT INTO	TBL_BOARD_n (		bltnbd_cd
		                       ,	title
		                       ,	ctnt
		                       ,	inqry_nmbr
		                       ,	rgst_dttm
		                       ,	rgst_id
		                       ,	rgst_mdcd
		                       ,	rgst_ip
		                       ,	fileatch_num)
	VALUES (
	
		#{boardCd}
		,#{title}
		,#{content}
		,'0'
		,SYSDATETIME
		,#{regId}
		,#{regMildsc}
		,#{regIp}
		,#{fileatch_num}
	)

mapper.delete:
	
	DELETE FROM	TBL_BOARD_n
	WHERE
		1=1
		AND	seq = #{seq}
		AND bltnbd_cd = #{boardCd}

mapper.select:
SELECT ROWNUM, A.* FROM (
SELECT A.SEQ , A.MDCD as MILDSC , A.ID as MNGR_ID , A.DEPT_CD , B.DEPT_NM , B.WHL_DEPT_NM as FULL_DEPT_NM FROM TBL_MNGR_n A LEFT JOIN TBL_DEPT_n B ON A.DEPT_CD = B.DEPT_CD AND A.MDCD = B.MDCD WHERE 1=1 AND A.ATHRT_NM = #{auth}
AND ( A.ID like '%' || #{searchTxt} ||'%' OR B.WHL_DEPT_NM like '%' || #{searchTxt} || '%' )
ORDER BY A.SEQ desc ) A WHERE 1=1 AND ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

SELECT COUNT(*) FROM TBL_MNGR_n A LEFT JOIN TBL_DEPT_n B ON A.DEPT_CD = B.DEPT_CD AND A.MDCD = B.MDCD WHERE 1=1 AND A.ATHRT_NM = #{auth}
AND A.MDCD = #{mildsc}
AND ( A.ID like '%' || #{searchTxt} ||'%' OR B.WHL_DEPT_NM like '%' || #{searchTxt} || '%' )
AND A.ID like '%' || #{searchTxt} ||'%'
AND B.WHL_DEPT_NM like '%' || #{searchTxt} ||'%'

mapper.insert:
	
	INSERT INTO	TBL_MNGR_n (	
		 mdcd
		,id
		,ecryptpw
		,whl_dept_cd_nm
		,dept_cd
		,athrt_nm
		,cnntn_failr_nmbr
		,cnntn_failr_dttm
		,rgst_dttm
	)
	VALUES (
		 REPLACE(#{mildsc},'1290451','A')
		,#{mngrId}
		,#{mngrPw}
		,#{fullDeptCd}
		,#{deptCd}
		,#{auth}
		,'0'
		,SYSDATETIME
		,SYSDATETIME
	)

mapper.update:
UPDATE TBL_MNGR_n SET mdcd = REPLACE(#{mildsc},'1290451','A'),
ecryptpw = #{mngrPw},
whl_dept_cd_nm = #{fullDeptCd}, dept_cd = #{deptCd} WHERE 1=1 AND seq = #{seq}

mapper.select:
SELECT ROWNUM, A.* FROM (
SELECT A.seq , A.MDCD AS mildsc , A.INSTLA_NM as facility_nm , A.dept_cd , A.TELNO AS tel , TO_CHAR(A.RGST_DTTM, 'YYYY-MM-DD') AS reg_dt , A.RGST_ID as reg_id , A.RGST_MDCD as reg_mildsc , A.RGST_IP as reg_ip , C.dept_nm , NVL(C.WHL_DEPT_NM, DECODE(A.MDCD , 'A', '국방부' , 'B', '육군' , 'C', '해군' , 'D', '공군')) AS full_dept_nm FROM tbl_facility_n A LEFT JOIN tbl_dept_n C ON A.dept_cd=C.dept_cd AND A.MDCD = C.MDCD WHERE 1=1
AND A.RGST_ID = #{regId}
AND A.MDCD = #{mildsc}
AND C.WHL_DEPT_NM LIKE '%' || (SELECT whl_dept_nm FROM tbl_dept_n WHERE dept_cd=#{deptCd}) || '%'
AND ( A.INSTLA_NM like '%' || #{searchTxt} ||'%' OR A.TELNO like '%' || #{searchTxt} || '%' OR C.WHL_DEPT_NM like '%' || #{searchTxt} || '%' OR A.RGST_ID like '%' || #{searchTxt} || '%' )
AND A.INSTLA_NM like '%' || #{searchTxt} ||'%'
AND A.TELNO like '%' || #{searchTxt} ||'%'
AND C.WHL_DEPT_NM like '%' || #{searchTxt} ||'%'
AND A.RGST_ID like '%' || #{searchTxt} ||'%'
ORDER BY A.INSTLA_NM, A.SEQ desc
) A WHERE 1=1 AND ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

mapper.insert:
	
	INSERT INTO	tbl_facility_n (	 
			MDCD
		,	INSTLA_NM
		,	dept_cd
		,	TELNO
		,	RGST_DTTM
		,	RGST_ID
		,	RGST_MDCD
		,	RGST_IP
		,	WHL_DEPT_CD_NM 
	)
	VALUES
		(
		 REPLACE(#{mildsc},'1290451','A') 
		,#{facilityNm}
		,#{deptCd}
		,#{tel}
		,SYSDATETIME
		,#{regId}
		,#{regMildsc}
		,#{regIp}
		,#{fullDeptCd}
	)

mapper.select:
SELECT ROWNUM, A.* FROM ( SELECT A.SEQ ,DECODE(A.MDCD , 'A', '국방부' , 'B', '육군' , 'C', '해군' , 'D', '공군') AS MILDSC_NM ,A.ID as MNGR_ID ,A.ECRYPTPW as MNGR_PW ,A.WHL_DEPT_CD_NM as FULL_DEPT_CD ,A.DEPT_CD ,B.WHL_DEPT_NM as FULL_DEPT_NM ,A.ATHRT_NM as AUTH ,TO_CHAR(A.RGST_DTTM, 'YYYY-MM-DD') AS REG_DT ,A.CNNTN_FAILR_NMBR as CONN_FAIL_CNT ,A.CNNTN_FAILR_DTTM as CONN_FAIL_DT FROM TBL_MNGR_n A LEFT JOIN TBL_DEPT_n B ON A.DEPT_CD = B.DEPT_CD AND A.MDCD = B.MDCD WHERE 1=1 AND A.ATHRT_NM = #{auth}
AND A.MDCD = #{mildsc}
AND ( A.ID like '%' || #{searchTxt} ||'%' OR B.WHL_DEPT_NM like '%' || #{searchTxt} || '%' )
AND A.ID like '%' || #{searchTxt} ||'%'
AND B.WHL_DEPT_NM like '%' || #{searchTxt} ||'%'
ORDER BY A.ID, A.SEQ desc ) A WHERE 1=1 AND ROWNUM BETWEEN #{firstIndex}+1 AND #{lastIndex}

mapper.update:
	
	UPDATE	TBL_MNGR_n
	SET
		ECRYPTPW = #{mngrPw}
	WHERE
		1=1
		AND	id = #{mngrId}

mapper.insert:
INSERT  INTO tbl_board_atch (
        tbl_nm
     ,  tbl_pk
     ,  loc_fl_nm
     ,  svr_fl_nm
     ,  svr_sv_pth
     ,  fl_sz
     ,  use_yn
     ,  reg_dt
     ,  reg_tm
     ,  reg_id
     ,  upt_dt
     ,  upt_tm
     ,  upt_id
     
     ,  tbl_pk2
     
  ) VALUES (
        #{tbl_nm}
     ,  #{tbl_pk}
     ,  #{locfl_nm}
     ,  #{svrfl_nm}
     ,  #{svrfl_pth}
     ,  #{fl_sz}
     ,  'Y'
     ,  TO_CHAR(SYSDATE, 'YYYYMMDD')
     ,  TO_CHAR(SYSTIMESTAMP, 'HH24MISS')
     ,  #{id}
     ,  TO_CHAR(SYSDATE, 'YYYYMMDD')
     ,  TO_CHAR(SYSTIMESTAMP, 'HH24MISS')
     ,  #{id}
   
     ,  #{ tbl_pk2 }
   
)

mapper.update:
UPDATE  tbl_board_atch
   SET  tbl_nm = #{tbl_nm}
     ,  tbl_pk = #{tbl_pk}
     ,  loc_fl_nm = #{locfl_nm}
     ,  svr_fl_nm = #{svrfl_nm}
     ,  svr_sv_pth = #{svrfl_pth}
     ,  fl_sz = #{fl_sz}
     ,  use_yn = #{use_yn}
     ,  upt_dt = TO_CHAR(SYSDATE, 'YYYYMMDD')
     ,  upt_tm = TO_CHAR(SYSTIMESTAMP, 'HH24MISS')
     ,  upt_id = #{id}
 WHERE  fl_id = #{fl_id}

mapper.select:
SELECT ROWNUM ROW_NO , T1.FL_ID , T1.TBL_NM , T1.TBL_PK , T1.TBL_PK2 , T1.LOC_FL_NM , T1.SVR_FL_NM , T1.SVR_SV_PTH , T1.FL_SZ , T1.USE_YN , REG_DT , REG_TM , REG_ID , UPT_DT , UPT_TM , UPT_ID , 'X' AS FILE_DELETE , IF(LENGTH(FL_SZ) < 4, FL_SZ, CEIL(FL_SZ/1024)|| 'KB') AS FL_KB_SZ FROM TBL_BOARD_ATCH T1 WHERE 1 = 1 AND USE_YN = 'Y' AND T1.TBL_NM = #{tbl_nm} AND T1.TBL_PK = #{tbl_pk}
AND T1.TBL_PK2 = #{tbl_pk2}
ORDER BY REG_DT ASC, REG_TM ASC
