mapper.select:

	SELECT  COUNT(*) 
	FROM 	TBL_USER_N 
	WHERE   1=1
	AND 	MDCD = #{mildsc}
	AND 	DEPT_CD = #{deptCd}
	ORDER BY DEPT_CD, rsponm


	SELECT COUNT(*) 
	FROM TBL_facility_n 
	WHERE 1=1
	AND mdcd = #{mildsc}
	AND DEPT_CD = #{deptCd}
	ORDER BY DEPT_CD

