mapper.select:
SELECT * FROM ( SELECT call_type , employee_id , ani , abandon_time , dialring_time , call_time , target_dn , uei , event_starttime FROM ot_callstat WHERE 1=1
AND employee_id = #{Usrgbcd}
AND employee_id != 0
AND employee_id = #{id}
AND LENGTH(ani) <= 10
AND LENGTH(ani) > 10
AND SUBSTR(event_starttime, 0,8) = TO_CHAR(sysdate,'YYYYMMDD') AND SUBSTR(event_starttime, 9,2) BETWEEN #{startDt} AND #{endDt}
AND SUBSTR(event_starttime, 0,6) BETWEEN #{startDt} AND #{endDt} AND SUBSTR(event_starttime, 0,8) BETWEEN #{startDt} AND #{endDt}
ORDER BY event_starttime DESC ) WHERE ROWNUM <= 500

SELECT * FROM ( SELECT call_type , employee_id , ani , abandon_time , dialring_time , call_time , target_dn , uei , event_starttime FROM ot_callstat WHERE 1=1
AND employee_id = #{Usrgbcd}
AND employee_id != 0
AND employee_id = #{id}
AND LENGTH(ani) <= 10
AND LENGTH(ani) > 10
AND SUBSTR(event_starttime, 0,8) = TO_CHAR(sysdate,'YYYYMMDD') AND SUBSTR(event_starttime, 9,2) BETWEEN #{startDt} AND #{endDt}
AND SUBSTR(event_starttime, 0,6) BETWEEN #{startDt} AND #{endDt} AND SUBSTR(event_starttime, 0,8) BETWEEN #{startDt} AND #{endDt}
ORDER BY event_starttime DESC ) WHERE ROWNUM <= 500

