mapper.select:
SELECT ROWNUM, A.* FROM ( SELECT seq, [type], fulnm, mdcd, servno, dept_cd, dept_nm, full_dept_nm, rsponm, rank_nm, telno, mpno, DATE_FORMAT(strt_date, '%Y-%m-%d') as strt_date, DATE_FORMAT(end_date, '%Y-%m-%d') as end_date, TO_CHAR(rgst_dttm, 'YYYY-MM-DD HH24:MI' ) AS rgst_dttm, rgst_rsn, rgst_id, act_type, TO_CHAR(act_dttm, 'YYYY-MM-DD HH24:MI' ) AS act_dttm, rtn_rsn, act_id FROM tbl_block_user WHERE 1=1
AND act_type = #{blockStat}
AND [type] = #{blockType}
AND fulnm LIKE '%' || #{blockSearchContent} || '%'
AND (REPLACE(TELNO,'-','') like '%' || #{blockSearchContent} || '%' OR REPLACE(MPNO, '-', '') like '%' || #{blockSearchContent} || '%' )
AND ((REPLACE(TELNO,'-','') like '%' || #{blockSearchContent} || '%' OR REPLACE(MPNO, '-', '') like '%' || #{blockSearchContent} || '%' ) OR fulnm LIKE '%' || #{blockSearchContent} || '%')
AND TO_CHAR(rgst_dttm, 'YYYYMMDD') BETWEEN #{startDt} AND #{endDt}
AND (strt_date <= #{endDt} AND end_date >= #{startDt} )
AND rgst_id =#{usrId}
AND act_type != 5
ORDER BY rgst_dttm DESC ) A WHERE ROWNUM BETWEEN #{startRowNum} AND #{endRowNum}

SELECT  COUNT(*) 
FROM 	TBL_BLOCK_USER 
WHERE   1=1

AND act_type = #{blockStat}


AND [type] = #{blockType}


	
		
		    AND fulnm LIKE '%' || #{blockSearchContent} || '%'
		
		
		    AND (REPLACE(TELNO,'-','') like '%' || #{blockSearchContent} || '%' OR REPLACE(MPNO, '-', '') like '%' || #{blockSearchContent} || '%' )
		
	
	
	AND ((REPLACE(TELNO,'-','') like '%' || #{blockSearchContent} || '%' OR REPLACE(MPNO, '-', '') like '%' || #{blockSearchContent} || '%' ) OR fulnm LIKE '%' || #{blockSearchContent} || '%')
	


    AND TO_CHAR(rgst_dttm, 'YYYYMMDD') BETWEEN #{startDt} AND #{endDt}


    AND (strt_date  <=  #{endDt} AND end_date  >=  #{startDt} )


    AND rgst_id =#{usrId}


    AND act_type != 5

