mapper.select:
SELECT * FROM ( SELECT B.dept_nm, A.email, B.whl_dept_nm AS full_dept_nm, A.id, A.mildsc_nm, A.mpno, A.nm, A.RANK, A.rsponm, A.telno FROM ( SELECT A.email, A.dept_cd, A.srvno AS id, DECODE(A.MDCD, 'A', '국방부', 'B', '육군', 'C', '해군', 'D', '공군') AS mildsc_nm, A.mpno, A.fulnm AS nm, A.rank_nm AS RANK, DECODE(A.MDCD, 'A', A.rsponm, A.rspsblt_biznes_nm) AS rsponm, A.telno FROM tbl_user_n A WHERE 1 = 1 AND A.telno LIKE '%' || #{searchContent} || '%'
) A INNER JOIN tbl_dept_n B ON A.dept_cd = B.dept_cd UNION ALL SELECT C.dept_nm, '', NVL(C.WHL_DEPT_NM, DECODE(A.MDCD, 'A', '국방부', 'B', '육군', 'C', '해군', 'D', '공군')) AS full_dept_nm, '', DECODE(A.MDCD, 'A', '국방부', 'B', '육군', 'C', '해군', 'D', '공군') AS mildsc_nm, '', A.INSTLA_NM, '', '시설물', A.telno FROM ( SELECT telno, INSTLA_NM, MDCD, dept_cd FROM tbl_facility_n WHERE 1 = 1 AND telno LIKE '%' || #{searchContent} || '%'
) A INNER JOIN tbl_dept_n C ON A.dept_cd = C.dept_cd ) WHERE ROWNUM <= #{searchCnt} ORDER BY telno, nm
