mapper.select:

	SELECT  COUNT(*) 
	FROM 	TBL_USER_N 
	WHERE   1=1
	AND 	MDCD = #{mildsc}
	AND 	DEPT_CD = #{deptCd}
	ORDER BY DEPT_CD, rsponm


	SELECT COUNT(*) 
	FROM TBL_facility_n 
	WHERE 1=1
	AND mdcd = #{mildsc}
	AND DEPT_CD = #{deptCd}
	ORDER BY DEPT_CD

mapper.update:

UPDATE TBL_BOARD_N 
SET
 INQRY_NMBR = (SELECT INQRY_NMBR+1 FROM TBL_BOARD_N WHERE 1=1 AND SEQ = #{seq}	AND BLTNBD_CD = #{boardCd})
WHERE 1=1
AND SEQ = #{seq}
AND BLTNBD_CD = #{boardCd}

